      this.Jump_Animation = instanceProperties[ "Jump Animation" ];
      this.Falling_Animation = instanceProperties[ "Falling Animation" ];
      this.Walking_Animation = instanceProperties[ "Walking Animation" ];


      // Boot phase.
      this.camera = this.scene.cameras.main;
      this.animation_key = this.owner.data.list.animKeyPrefix;
