      this.scene.getKey( 40 ).off( "down", this.onKeyInput40, this );
      this.scene.getKey( 38 ).off( "down", this.onKeyInput38, this );
      this.scene.getKey( 40 ).off( "up", this.onKeyInput402, this );
      this.scene.getKey( 38 ).off( "up", this.onKeyInput382, this );
      this.scene.getKey( 13 ).off( "down", this.onKeyInput13, this );
      this.scene.getKey( 37 ).off( "down", this.onKeyInput37, this );
      this.scene.getKey( 13 ).off( "up", this.onKeyInput132, this );
