      this.owner.tags.add( 'hazard' );
      if (this.Center_AnchorPoint_on_flip) {
        this.owner.anchorX = ( this.owner.width / 2.4 );
      }
      if (this.Lives == null) {
        this.Lives = 1;
      }
      this.createKeyValues(  );
      this.snapCameraBounds(  );
    }

