      }
    }

    executeMessageSTOP_RIGHT () {
      // Executed when the message 'STOP_RIGHT' is received.
      if (this.inputEnabled) {
        this.stopRight(  );
      }
    }

    onUpdate () {
      // Executed every frame.
      this.executeHorizontalCommands(  );
      this.executeVerticalCommands(  );
      this.setCorrectAnimation(  );
      this.centerCameraOnMe(  );
    }

    executeMessageSTART_DOWN () {
      // Executed when the message 'START_DOWN' is received.
      if (this.inputEnabled) {
        this.startDown(  );
      }
    }

    executeMessageSTART_UP () {
      // Executed when the message 'START_UP' is received.
      if (this.inputEnabled) {
        this.startUp(  );
      }
    }

    onKeyInput13 () {
      if (this.inputEnabled) {
        this.startAction(  );
      }
    }

    onKeyInput37 () {
      if (this.inputEnabled) {
        this.startLeft(  );
      }
    }

    onKeyInput132 () {
      if (this.inputEnabled) {
        this.stopAction(  );
      }
    }

    onKeyInput372 () {
      if (this.inputEnabled) {
        this.stopLeft(  );
      }
    }

    executeMessageSTOP_DOWN () {
      // Executed when the message 'STOP_DOWN' is received.
      if (this.inputEnabled) {
        this.stopDown(  );
      }
    }

    executeMessageSTART_ACTION () {
      // Executed when the message 'START_ACTION' is received.
      if (this.inputEnabled) {
        this.startAction(  );
      }
    }

    executeMessageSTOP_UP () {
      // Executed when the message 'STOP_UP' is received.
      if (this.inputEnabled) {
        this.stopUp(  );
      }
    }

    onKeyInput39 () {
      if (this.inputEnabled) {
        this.startRight(  );
      }
    }

    onKeyInput32 () {
      if (this.inputEnabled) {
        this.startAction(  );
      }
    }

    onKeyInput392 () {
      if (this.inputEnabled) {
        this.stopRight(  );
      }
    }

    onKeyInput322 () {
      if (this.inputEnabled) {
        this.stopAction(  );
      }
    }

    executeMessageSTOP_ACTION () {
      // Executed when the message 'STOP_ACTION' is received.
      if (this.inputEnabled) {
        this.stopAction(  );
      }
    }

    executeMessageINPUT_ENABLE () {
      // Executed when the message 'INPUT_ENABLE' is received.
      this.enableInput(  );
    }

    executeMessageINPUT_DISABLE () {
      // Executed when the message 'INPUT_DISABLE' is received.
      this.disableInput(  );
    }

    executeMessagePAUSE () {
      // Executed when the message 'PAUSE' is received.
      this.owner.setPhysics( false );
      this.unpaused = false;
    }

    executeMessageUNPAUSE () {
      // Executed when the message 'UNPAUSE' is received.
      this.owner.setPhysics( true );
      this.unpaused = true;
    }

    executeMessageACTION_LISTENER_REGISTER () {
      // Executed when the 'ACTION_LISTENER_REGISTER' is received.
      if (this.actionListeners.indexOf(this.listener) + 1 < 1) {
        if ( !this.actionListeners ) {
          this.reportError( "`Add to List` block could not find a list called [actionListeners]." );
          return;
        }
        this.actionListeners.push( this.listener );
      }
    }

    executeMessageREGEN () {
      // Executed when the 'REGEN' is received.
      if (this.Health != -1) {
        this.Health = Math.min(Math.max(this.Health + this.value, 0), this.Max_Health);
        this.updateKeys(  );
        if (this.Health == 0) {
          this.scene.messageInstance( this.owner, 'DIE' );
        }
      }
    }

    executeMessageACTION_LISTENER_DEREGISTER () {
      // Executed when the 'ACTION_LISTENER_DEREGISTER' is received.
      this.listenerIndex = this.actionListeners.indexOf(this.listener) + 1;
      if (this.listenerIndex > 0) {
        this.actionListeners.splice(this.listenerIndex - 1, 1);
      }
    }

    executeMessageDAMAGE () {
      // Executed when the 'DAMAGE' is received.
      if (this.Health != -1) {
        this.Health = Math.min(Math.max(this.Health - this.value, 0), this.Max_Health);
        this.updateKeys(  );
        if (this.Health == 0) {
          this.scene.messageInstance( this.owner, 'DIE' );
        }
      }
    }

    executeMessageACTION_LISTENER_CLEAR () {
      // Executed when the message 'ACTION_LISTENER_CLEAR' is received.
      this.actionListeners = [];
    }

    executeMessagePLAYER_DIE_INTERRUPT () {
      // Executed when the message 'PLAYER_DIE_INTERRUPT' is received.
      this.continueWithPlayerDeath = false;
    }

    executeMessageDIE () {
      // Executed when the message 'DIE' is received.
      if (this.owner.playing() != this.Dying_Animation) {
        if (this.Health >= 0) {
          this.Health = 0;
          this.updateKeys(  );
          this.Health = -1;
          this.disableInput(  );
          this.owner.playMy( this.Dying_Animation );
          this.scene.broadcast( 'PLAYER_DYING', this.owner );
          this.delayed_event2 = this.scene.time.delayedCall( 16, function() {
            if ( !this.owner || this.owner.exists === false ) {
              return;
            }
              if (this.owner.playing() != this.Dying_Animation) {
              this.doDeathFunctionality(  );
            }
          }, null, this );
        }
      }
    }

    executeMessageLIVES () {
      // Executed when the 'LIVES' is received.
      this.Lives = this.Lives + this.value;
      this.owner.properties.set( 'lives',this.Lives );
    }

    executeMessageRESPAWN () {
      // Executed when the message 'RESPAWN' is received.
      this.respawnPlayer(  );
    }

    executeMessageREQUEST_HAND_OFFSET_X () {
      // Executed when the 'REQUEST_HAND_OFFSET_X' is received.
      if (this.workingProne) {
        this.scene.messageInstance( this.entity, 'PROVIDE_HAND_OFFSET_X', (this.Hand_Offset_X + 20) );
      } else {
        this.scene.messageInstance( this.entity, 'PROVIDE_HAND_OFFSET_X', this.Hand_Offset_X );
      }
    }

    executeMessageREQUEST_HAND_OFFSET_Y () {
