      } else {
        this.scene.messageInstance( this.entity, 'PROVIDE_HAND_OFFSET_Y', this.Hand_Offset_Y );
      }
    }

    validateInstanceVariables (  ) {
      if (this.Default_Gravity_X == null) {
        this.Default_Gravity_X = 0;
      }
      if (this.Default_Gravity_Y == null) {
        this.Default_Gravity_Y = 1100;
      }
      if (this.Jump_Velocity == null) {
        this.Jump_Velocity = 600;
      }
      if (this.Walkspeed_Per_Second == null) {
        this.Walkspeed_Per_Second = 600;
      }
    }

    centerCameraOnMe (  ) {
      if (this.Center_Camera_on_Me) {
        this.lookDown(  );
        this.camera.posX = ((this.owner.posX + this.owner.anchorX) - this.camera.width / 2);
        this.camera.posY = (((this.owner.posY + this.owner.anchorY) - this.camera.height / 2) + this.cameraOffsetY);
      }
    }

    createKeyValues (  ) {
      this.owner.properties.set( 'lives',this.Lives );
      if (!this.owner.properties.get( 'respawn-location' )) {
        this.owner.properties.set( 'respawn-location',{ x: this.owner.posX, y: this.owner.posY } );
      }
      if (!this.owner.properties.get( 'score' )) {
        this.owner.properties.set( 'score',0 );
      }
      if (this.Max_Health < 0) {
        this.Max_Health = 100;
      }
      this.Health = Math.min(Math.max(this.Health, 1), this.Max_Health);
      this.updateKeys(  );
    }

    lookDown (  ) {
      if (this.unpaused) {
        if (this.workingProne && ( this.owner.body.touching.down || this.owner.body.blocked.down )) {
          this.heldDown = this.heldDown + ( ( this.scene.data.get( "delta" ) || 1000 / 60 ) * 60 / 1000 ) * this.lookDownSpeed;
        } else {
          this.heldDown = -this.heldDownDelay;
        }
        this.cameraOffsetY = (Math.min(Math.max(this.heldDown, 0), this.camera.height / 2) - this.cameraOffsetY) * this.lookDownSmoothness + this.cameraOffsetY;
      }
    }

    reportError( message ) {
      message = "(" + this.name.replace( /_[\d]+$/, "" ) + ")" + message;
      console.trace( message );
      this.game.reportError( message, message, "SCRIPT ERROR" );
    }

    errorCheckNotNull( input, backup, message ) {
      if( !input ) {
        this.reportError( message );
        return backup;
      }
      return input;
    }

    errorCheckNotNull2( input, backup, message ) {
      if( !input ) {
        this.reportError( message );
        return backup;
      }
      return input;
    }

    snapCameraBounds (  ) {
      if (this.Snap_Camera_Dimenisions) {
        this.camera.bounds.left = 0;
        this.camera.bounds.top = 0;
        this.camera.bounds.right = ((this.errorCheckNotNull( this.Max_Camera, { x: 0, y: 0 }, "`Get X/Y of Location` block could not find a location named [Max_Camera].")).x - this.camera.width) + this.camera.width;
        this.camera.bounds.bottom = ((this.errorCheckNotNull2( this.Max_Camera, { x: 0, y: 0 }, "`Get X/Y of Location` block could not find a location named [Max_Camera].")).y - this.camera.height) + this.camera.height;
      }
    }

    updateKeys (  ) {
      this.owner.properties.set( 'health',this.Health );
      this.owner.properties.set( 'max-health',this.Max_Health );
    }

    onMessageReceived ( name, message ) {

      if ( message === 'START_LEFT' ) {
        this.executeMessageSTART_LEFT();
      }

      if ( message === 'STOP_LEFT' ) {
        this.executeMessageSTOP_LEFT();
      }

      if ( message === 'START_RIGHT' ) {
        this.executeMessageSTART_RIGHT();
      }

      if ( message === 'STOP_RIGHT' ) {
