      if ( message === 'START_UP' ) {
        this.executeMessageSTART_UP();
      }

      if ( message === 'STOP_DOWN' ) {
        this.executeMessageSTOP_DOWN();
      }

      if ( message === 'START_ACTION' ) {
        this.executeMessageSTART_ACTION();
      }

      if ( message === 'STOP_UP' ) {
        this.executeMessageSTOP_UP();
      }

      if ( message === 'STOP_ACTION' ) {
        this.executeMessageSTOP_ACTION();
      }

      if ( message === 'INPUT_ENABLE' ) {
        this.executeMessageINPUT_ENABLE();
      }

      if ( message === 'INPUT_DISABLE' ) {
        this.executeMessageINPUT_DISABLE();
      }

      if ( message === 'PAUSE' ) {
        this.executeMessagePAUSE();
      }

      if ( message === 'UNPAUSE' ) {
        this.executeMessageUNPAUSE();
      }

      if ( message === 'ACTION_LISTENER_REGISTER' ) {
        this.listener = this.owner.properties.get( "_messaging-value_" );
        this.executeMessageACTION_LISTENER_REGISTER();
      }

      if ( message === 'REGEN' ) {
        this.value = this.owner.properties.get( "_messaging-value_" );
        this.executeMessageREGEN();
      }

      if ( message === 'ACTION_LISTENER_DEREGISTER' ) {
        this.listener = this.owner.properties.get( "_messaging-value_" );
        this.executeMessageACTION_LISTENER_DEREGISTER();
      }

      if ( message === 'DAMAGE' ) {
        this.value = this.owner.properties.get( "_messaging-value_" );
        this.executeMessageDAMAGE();
      }

      if ( message === 'ACTION_LISTENER_CLEAR' ) {
        this.executeMessageACTION_LISTENER_CLEAR();
      }

      if ( message === 'PLAYER_DIE_INTERRUPT' ) {
        this.executeMessagePLAYER_DIE_INTERRUPT();
      }

      if ( message === 'DIE' ) {
        this.executeMessageDIE();
      }

      if ( message === 'LIVES' ) {
        this.value = this.owner.properties.get( "_messaging-value_" );
        this.executeMessageLIVES();
      }

      if ( message === 'RESPAWN' ) {
        this.executeMessageRESPAWN();
      }

      if ( message === 'REQUEST_HAND_OFFSET_X' ) {
        this.entity = this.owner.properties.get( "_messaging-value_" );
        this.executeMessageREQUEST_HAND_OFFSET_X();
      }

      if ( message === 'REQUEST_HAND_OFFSET_Y' ) {
        this.entity = this.owner.properties.get( "_messaging-value_" );
        this.executeMessageREQUEST_HAND_OFFSET_Y();
      }

    }

    setCorrectAnimation (  ) {
      if (this.unpaused) {
        this.flipPlayer(  );
        if (this.owner.playing() != this.Dying_Animation) {
          if (( this.owner.body.touching.down || this.owner.body.blocked.down )) {
            if (this.workingProne) {
              if (this.owner.playing() != this.Prone_Animation) {
                this.owner.playMy( this.Prone_Animation );
              }
            } else {
              if (this.workingDirection != 0) {
                if (this.owner.playing() != this.Walking_Animation) {
                  this.owner.playMy( this.Walking_Animation );
                }
              } else {
                if (this.owner.playing() != this.Idle_Animation) {
                  this.owner.playMy( this.Idle_Animation );
                }
              }
            }
          } else {
            if (this.owner.body.velocity.y < 0) {
              if (this.owner.playing() != this.Jump_Animation) {
                this.owner.playMy( this.Jump_Animation );
              }
            } else {
              if (this.owner.playing() != this.Falling_Animation) {
                this.owner.playMy( this.Falling_Animation );
              }
            }
          }
        }
      }
    }

    executeVerticalCommands (  ) {
      if (( this.owner.body.touching.down || this.owner.body.blocked.down )) {
        if (this.workingJump) {
          this.owner.body.velocity.y = (-this.Jump_Velocity);
          this.owner.posY = ( this.owner.posY - 1 );
        }
      }
    }

    executeHorizontalCommands (  ) {
      if (this.workingProne && ( this.owner.body.touching.down || this.owner.body.blocked.down )) {
        this.owner.body.velocity.x = 0;
      } else {
        this.owner.body.velocity.x = (this.Walkspeed_Per_Second * this.workingDirection);
      }
    }

    startAction (  ) {
      this.workingAction = true;
      var listener_list = this.actionListeners;
      for (var listener_index in listener_list) {
        this.listener = listener_list[listener_index];
        this.scene.messageInstance( this.listener, 'ACTION_START' );
      }
    }

    flipPlayer (  ) {
      if (this.workingDirection != 0) {
        this.owner.scaleX = ( Math.abs(this.owner.scaleX) * this.workingDirection );
        if (this.Center_AnchorPoint_on_flip) {
          this.owner.anchorX = ( this.owner.width / 2.4 );
        }
      }
    }

    stopAction (  ) {
      this.workingAction = false;
      var listener_list2 = this.actionListeners;
      for (var listener_index2 in listener_list2) {
        this.listener = listener_list2[listener_index2];
        this.scene.messageInstance( this.listener, 'ACTION_STOP' );
      }
    }

    enableInput (  ) {
      this.inputEnabled = true;
    }

    disableInput (  ) {
      this.inputEnabled = false;
      this.stopAction(  );
      this.stopDown(  );
      this.stopLeft(  );
      this.stopRight(  );
      this.stopUp(  );
    }

    startUp (  ) {
      this.workingJump = true;
    }

    stopUp (  ) {
      this.workingJump = false;
    }

    startLeft (  ) {
      this.workingDirection = -1;
    }

    startRight (  ) {
      this.workingDirection = 1;
    }

    stopLeft (  ) {
      if (this.workingDirection == -1) {
        this.workingDirection = 0;
      }
    }

    stopRight (  ) {
      if (this.workingDirection == 1) {
        this.workingDirection = 0;
      }
    }

    startDown (  ) {
      this.workingProne = true;
    }

    stopDown (  ) {
      this.workingProne = false;
    }

    doDeathFunctionality (  ) {
      this.owner.visible = false;
      this.owner.playStop();
      this.delayed_event = this.scene.time.delayedCall( 1000, function() {
        if ( !this.owner || this.owner.exists === false ) {
          return;
        }
          this.continueWithPlayerDeath = true;
        this.scene.broadcast( 'PLAYER_DIE', this.owner );
        if (this.continueWithPlayerDeath) {
          this.Lives = this.Lives - 1;
          this.owner.properties.set( 'lives',this.Lives );
          if (this.Lives > 0) {
            this.respawnPlayer(  );
          } else {
            this.game.levelManager.switchTo( ( this.game.levelManager.levels.length - 1 ) + 1 );
          }
        }
